scripts.download:
curl 'https://api.baseql.com/airtable/graphql/appHENMf239lCORVM' -H 'Content-Type: application/json' -H 'Accept: application/json' -H 'Connection: keep-alive' -H 'DNT: 1' -H 'Origin: https://tianpan.co' --data-binary '{"query":"query Projects {
  projects {
    id
    name
    introduction
    website
    twitter
    marketCap
    tokenSymbol
  logoUrl
  explorerUrl
    mediumUrl
    redditUrl
    githubUrl
    whitepaperUrl
    tags {
      id
      name
    }
    chains {
      id
      name
    }
  }
}"}' --compressed > src/projects-data.json        &&          curl 'https://api.baseql.com/airtable/graphql/appHENMf239lCORVM' -H 'Content-Type: application/json' -H 'Accept: application/json' -H 'Connection: keep-alive' -H 'DNT: 1' -H 'Origin: https://tianpan.co' --data-binary '{"query":"query Tags {
  tags {
    id
    name
    description
  }
}"}' --compressed > scripts/tags-data.json                &&                  curl 'https://api.baseql.com/airtable/graphql/appHENMf239lCORVM' -H 'Content-Type: application/json' -H 'Accept: application/json' -H 'Connection: keep-alive' -H 'DNT: 1' -H 'Origin: https://tianpan.co' --data-binary '{"query":"query Chains {
  chains {
    id
    name
    description
  }
}"}' --compressed > scripts/chains-data.json
